import React, { useState, useEffect } from 'react';
import { loginStudent } from './..services/studentService'; // Ensure correct path

const StudentLogin = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [studentInfo, setStudentInfo] = useState(null);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginStudent(credentials);
      console.log('Login Response:', response.data); // Debugging log
      if (response.data.student) {
        setStudentInfo(response.data.student);
        alert(response.data.message);
      } else {
        throw new Error('Unexpected response format');
      }
    } catch (error) {
      console.error('There was an error logging in!', error);
      setError('Login failed. Please check your credentials and try again.');
    }
  };

  useEffect(() => {
    console.log('Student Info:', studentInfo); // Verify state update
  }, [studentInfo]);

  return (
    <div className="container mt-5">
      <h2>Student Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={credentials.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            className="form-control"
            id="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Login</button>
      </form>

      {error && <p className="error">{error}</p>}

      {studentInfo && (
        <div className="mt-4">
          <h3>Student Details</h3>
          <p><strong>Student ID:</strong> {studentInfo.student_id}</p>
          <p><strong>Name:</strong> {studentInfo.student_name}</p>
          <p><strong>Phone Number:</strong> {studentInfo.phone_no}</p>
          <p><strong>Department:</strong> {studentInfo.department}</p>
          <p><strong>Marks:</strong> {studentInfo.marks}</p>
          <p><strong>Email:</strong> {studentInfo.email}</p>
        </div>
      )}
    </div>
  );
};

export default StudentLogin;
