// components/Student/StudentDashboard.js
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const StudentDashboard = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const student = state?.student;

  if (!student) return <p>No student data available.</p>;

  const handleUpdate = () => {
    navigate(`/student/update/${student.student_id}`, { state: { student } });
  };

  return (
    <div className="container mt-5">
      <h3>Welcome, {student.student_name}</h3>
      <p><strong>Student ID:</strong> {student.student_id}</p>
      <p><strong>Phone Number:</strong> {student.phone_no}</p>
      <p><strong>Department:</strong> {student.department}</p>
      <p><strong>Marks:</strong> {student.marks}</p>
      <p><strong>Email:</strong> {student.email}</p>

      <button className="btn btn-warning mt-3" onClick={handleUpdate}>
        Update Details
      </button>
    </div>
  );
};

export default StudentDashboard;
