import React, { useState } from 'react';
import { deleteFaculty, registerFaculty } from './../services/facultyService';

const FacultyRegister = () => {
  const [formData, setFormData] = useState({
    faculty_id: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await deleteFaculty(formData);
      alert('Faculty deleted successfully!');
      setFormData({
        faculty_id: ''
      }); // Clear form
    } catch (error) {
      console.error('Error registering faculty:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Register Faculty</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="faculty_id">Faculty ID</label>
          <input
            type="text"
            className="form-control"
            id="faculty_id"
            name="faculty_id"
            value={formData.faculty_id}
            onChange={handleChange}
            placeholder="Enter Faculty ID"  // Added placeholder
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="faculty_name">Faculty Name</label>
          <input
            type="text"
            className="form-control"
            id="faculty_name"
            name="faculty_name"
            value={formData.faculty_name}
            onChange={handleChange}
            placeholder="Enter Faculty Name"  // Added placeholder
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Register</button>
      </form>
    </div>
  );
};

export default FacultyDelete;
