import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Visualizations = () => {
  const [barChart, setBarChart] = useState('');
  const [pieChart, setPieChart] = useState('');

  useEffect(() => {
    // Fetch visualizations (bar and pie charts) from the Flask backend
    axios.get('http://localhost:5000/visualizations')
      .then(response => {
        setBarChart(response.data.bar_image);
        setPieChart(response.data.pie_image);
      })
      .catch(error => {
        console.error('Error fetching visualizations:', error);
      });
  }, []);

  return (
    <div className="container mt-5">
      <h2>Feedback Visualizations</h2>
      <div>
        {barChart && (
          <img src={`data:image/png;base64,${barChart}`} alt="Bar Chart" className="img-fluid mb-4" />
        )}
        {pieChart && (
          <img src={`data:image/png;base64,${pieChart}`} alt="Pie Chart" className="img-fluid mb-4" />
        )}
      </div>
    </div>
  );
};

export default Visualizations;
