import React, { useState } from 'react';
import { loginStudent } from './studentService'; // Ensure this imports correctly

const StudentLogin = () => {
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [studentInfo, setStudentInfo] = useState(null);
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await loginStudent(credentials);

      // Log the entire response for debugging purposes
      console.log('Login Response:', response);

      // Check if response.data contains the expected fields
      if (response.data && response.data.student_id) {
        setStudentInfo(response.data);
        alert('Login successful!');
      } else {
        throw new Error('Unexpected response format');
      }
    } catch (error) {
      console.error('There was an error logging in:', error);
      setError('Login failed. Please check your credentials and try again.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  return (
    <div>
      <h2>Student Login</h2>
      <input
        type="text"
        name="username"
        value={credentials.username}
        onChange={handleChange}
        placeholder="Username"
      />
      <input
        type="password"
        name="password"
        value={credentials.password}
        onChange={handleChange}
        placeholder="Password"
      />
      <button onClick={handleLogin}>Login</button>
      {error && <p className="error">{error}</p>}
      {studentInfo && (
        <div>
          <h3>Welcome, {studentInfo.student_name}</h3>
          <p>Student ID: {studentInfo.student_id}</p>
        </div>
      )}
    </div>
  );
};

export default StudentLogin;
