import React, { useState } from 'react';
import { updateFaculty } from './../services/facultyService';
import { useNavigate } from 'react-router-dom';

const FacultyUpdate = () => {
  const [formData, setFormData] = useState({
    faculty_id: '',
    faculty_name: '',
    department: '',
    email: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await updateFaculty(formData)
      alert('Faculty updated successfully!');
      setFormData({
          faculty_id: '',
          faculty_name: '',
          department: '',
          email: ''
        });
        navigate('/faculty/login');
    } catch (error) {
      console.error('Error updating faculty:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Update Faculty</h2>
      <form onSubmit={handleSubmit}>
      <div className="form-group">
          <label htmlFor="faculty_id">Faculty ID</label>
          <input
            type="text"
            className="form-control"
            id="faculty_id"
            name="faculty_id"
            value={formData.faculty_id}
            onChange={handleChange}
            placeholder="Enter Faculty ID"  
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="faculty_name">Faculty Name</label>
          <input
            type="text"
            className="form-control"
            id="faculty_name"
            name="faculty_name"
            value={formData.faculty_name}
            onChange={handleChange}
            placeholder="Enter Faculty Name"  
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="department">Department</label>
          <input
            type="text"
            className="form-control"
            id="department"
            name="department"
            value={formData.department}
            onChange={handleChange}
            placeholder="Enter Department Name"  
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter Email Address"  
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Update</button>
      </form>
    </div>
  );
};

export default FacultyUpdate;
