import React, { useState, useEffect } from 'react';
import { loginStudent } from './../services/studentService';

const StudentLogin = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [studentInfo, setStudentInfo] = useState(null);

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginStudent(credentials);
      alert('Login successful!');
      setStudentInfo(response.data.student);
      console.log('Login Response:', response.data);
    } catch (error) {
      console.error('There was an error logging in!', error);
    }
  };

  useEffect(() => {
    console.log('Student Info:', studentInfo);
  }, [studentInfo]);

  return (
    <div className="container mt-5">
      {!studentInfo ? (
        <>
          <h2>Student Login</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                className="form-control"
                id="email"
                name="email"
                value={credentials.email}
                onChange={handleChange}
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                className="form-control"
                id="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                placeholder="Enter your password"
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Login</button>
          </form>
        </>
      ) : (
        <div className="mt-4">
          <h3>Student Details</h3>
          <p><strong>Student ID:</strong> {studentInfo.student_id}</p>
          <p><strong>Name:</strong> {studentInfo.student_name}</p>
          <p><strong>Phone Number:</strong> {studentInfo.phone_no}</p>
          <p><strong>Department:</strong> {studentInfo.department}</p>
          <p><strong>Marks:</strong> {studentInfo.marks}</p>
          <p><strong>Email:</strong> {studentInfo.email}</p>
        </div>
      )}
    </div>
  );
};

export default StudentLogin;
