import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';  // Import your custom styles
import StudentRegister from './components/Student/StudentRegister';
import StudentLogin from './components/Student/StudentLogin';
import StudentList from './components/Student/StudentList';
import StudentUpdate from './components/Student/StudentUpdate';
import FacultyRegister from './components/Faculty/FacultyRegister';
import FacultyLogin from './components/Faculty/FacultyLogin';
import FacultyList from './components/Faculty/FacultyList';
import FacultyUpdate from './components/Faculty/FacultyUpdate';
import FeedbackForm from './components/Feedback/FeedbackForm';
import CourseRegistration from './components/Course/CourseRegistration';
import CourseList from './components/Course/CourseList';
import CourseUpdate from './components/Course/CourseUpdate';
import ProtectedRoute from './components/ProtectedRoute';

function HomePage() {
  return (
    <div className="home-message">
      <h1>Welcome to College Management System</h1>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      setIsLoggedIn(false);
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <Navbar bg="dark" variant="dark" expand="lg">
            <Navbar.Brand as={Link} to="/">Project Management</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                <NavDropdown title="Student Service" id="studentDropdown" aria-haspopup="true">
                  <NavDropdown.Item as={Link} to="/student/login">Login</NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/student/register">Register</NavDropdown.Item>
                  {isLoggedIn && (
                    <>
                      <NavDropdown.Item as={Link} to="/students">List</NavDropdown.Item>
                      <NavDropdown.Item as={Link} to="/student/update/:id">Update</NavDropdown.Item>
                    </>
                  )}
                </NavDropdown>
                <NavDropdown title="Faculty Service" id="facultyDropdown" aria-haspopup="true">
                  <NavDropdown.Item as={Link} to="/faculty/login">Login</NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/faculty/register">Register</NavDropdown.Item>
                  {isLoggedIn && (
                    <>
                      <NavDropdown.Item as={Link} to="/faculties">List</NavDropdown.Item>
                      <NavDropdown.Item as={Link} to="/faculty/update/:id">Update</NavDropdown.Item>
                    </>
                  )}
                </NavDropdown>
                <NavDropdown title="Course Service" id="courseDropdown" aria-haspopup="true">
                  <NavDropdown.Item as={Link} to="/course/register">Register</NavDropdown.Item>
                  {isLoggedIn && (
                    <>
                      <NavDropdown.Item as={Link} to="/courses">List</NavDropdown.Item>
                      <NavDropdown.Item as={Link} to="/course/update/:id">Update</NavDropdown.Item>
                    </>
                  )}
                </NavDropdown>
                <NavDropdown title="Feedback" id="feedbackDropdown" aria-haspopup="true">
                  <NavDropdown.Item as={Link} to="/feedback?type=visual">Visual Representation</NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/feedback?type=faculty">Top Rated Faculty</NavDropdown.Item>
                  <NavDropdown.Item as={Link} to="/feedback?type=normal">Normal Feedback</NavDropdown.Item>
                </NavDropdown>
                {isLoggedIn && (
                  <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
                )}
              </Nav>
            </Navbar.Collapse>
          </Navbar>
        </header>
        <main className="container mt-5">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/student/register" element={<StudentRegister />} />
            <Route path="/student/login" element={<StudentLogin onLogin={handleLogin} />} />
            <Route path="/students" element={
              <ProtectedRoute isLoggedIn={isLoggedIn}>
                <StudentList />
              </ProtectedRoute>
            } />
            <Route path="/student/update/:id" element={
              <ProtectedRoute isLoggedIn={isLoggedIn}>
                <StudentUpdate />
              </ProtectedRoute>
            } />
            <Route path="/faculty/register" element={<FacultyRegister />} />
            <Route path="/faculty/login" element={<FacultyLogin onLogin={handleLogin} />} />
            <Route path="/faculties" element={
              <ProtectedRoute isLoggedIn={isLoggedIn}>
                <FacultyList />
              </ProtectedRoute>
            } />
            <Route path="/faculty/update/:id" element={
              <ProtectedRoute isLoggedIn={isLoggedIn}>
                <FacultyUpdate />
              </ProtectedRoute>
            } />
            <Route path="/feedback" element={<FeedbackForm />} />
            <Route path="/course/register" element={<CourseRegistration />} />
            <Route path="/courses" element={
              <ProtectedRoute isLoggedIn={isLoggedIn}>
                <CourseList />
              </ProtectedRoute>
            } />
            <Route path="/course/update/:id" element={
              <ProtectedRoute isLoggedIn={isLoggedIn}>
                <CourseUpdate />
              </ProtectedRoute>
            } />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
