import axios from 'axios';

const API_URL = 'http://localhost:3002'; // Replace with your API URL

export const registerFaculty = (facultyData) => {
  return axios.post(`${API_URL}/register`, facultyData);
};

export const loginFaculty = (credentials) => {
  return axios.post(`${API_URL}/login`, credentials);
};

export const getFaculties = () => {
  return axios.get(API_URL);
};

export const updateFaculty = (id, facultyData) => {
  return axios.put(`${API_URL}/${id}`, facultyData);
};
