import React, { useState } from 'react';
import { loginStudent } from './../services/studentService';

const StudentLogin = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [studentInfo, setStudentInfo] = useState(null); // State to store student information

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginStudent(credentials);
      alert('Login successful!');
      setStudentInfo(response.data); // Store student info from response
    } catch (error) {
      console.error('There was an error logging in!', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Student Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={credentials.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            className="form-control"
            id="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Login</button>
      </form>

      {studentInfo && (
        <div className="mt-4">
          <h3>Student Details</h3>
          <p><strong>Name:</strong> {studentInfo.name}</p>
          <p><strong>Email:</strong> {studentInfo.email}</p>
          {/* Add more fields as needed */}
        </div>
      )}
    </div>
  );
};

export default StudentLogin;
