import React, { useState, useEffect } from 'react';
import { loginFaculty } from './../services/facultyService';

const FacultyLogin = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [facultyInfo, setFacultyInfo] = useState(null);

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginFaculty(credentials);
      alert('Login successful!');
      setFacultyInfo(response.data.faculty); 
      console.log('Login Response:', response.data);
    } catch (error) {
      console.error('Error logging in:', error);
      alert('Login failed. Please check your credentials.');
    }
  };

  useEffect(() => {
    console.log('Faculty Info:', facultyInfo);
  }, [facultyInfo]);

  return (
    <div className="container mt-5">
      <h2>Faculty Login</h2>
      {!facultyInfo ? (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={credentials.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              placeholder="Enter your password"
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">Login</button>
        </form>
      ) : (
        <div className="mt-4">
          <h3>Faculty Details</h3>
          <p><strong>Faculty ID:</strong> {facultyInfo.faculty_id}</p>
          <p><strong>Name:</strong> {facultyInfo.faculty_name}</p>
          <p><strong>Department:</strong> {facultyInfo.department}</p>
          <p><strong>Email:</strong> {facultyInfo.email}</p>
        </div>
      )}
    </div>
  );
};

export default FacultyLogin;
