import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Form, Button, DropdownButton, Dropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const FeedbackForm = () => {
  const [feedbackData, setFeedbackData] = useState({
    name: '',
    email: '',
    message: '',
    feedbackType: '',
    rating: ''
  });

  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const feedbackType = queryParams.get('type');

  useEffect(() => {
    setFeedbackData((prevData) => ({
      ...prevData,
      feedbackType: feedbackType || ''
    }));
  }, [feedbackType]);

  const handleChange = (e) => {
    setFeedbackData({ ...feedbackData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Replace with your submission logic
      console.log(`Submitting: ${JSON.stringify(feedbackData)}`);
      alert('Feedback submitted successfully!');
      setFeedbackData({ name: '', email: '', message: '', feedbackType: '', rating: '' });
    } catch (error) {
      console.error('Error submitting feedback:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Submit Feedback</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="name">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={feedbackData.name}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={feedbackData.email}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="message">
          <Form.Label>Message</Form.Label>
          <Form.Control
            as="textarea"
            name="message"
            value={feedbackData.message}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="feedbackType">
          <Form.Label>Feedback Type</Form.Label>
          <DropdownButton
            id="dropdown-basic-button"
            title={feedbackData.feedbackType || "Select Feedback Type"}
            onSelect={(eventKey) => setFeedbackData({ ...feedbackData, feedbackType: eventKey })}
          >
            <Dropdown.Item eventKey="Top Rated Faculty">Top Rated Faculty</Dropdown.Item>
            <Dropdown.Item eventKey="Normal Feedback">Normal Feedback</Dropdown.Item>
            <Dropdown.Item eventKey="Visual Representation">Visual Representation</Dropdown.Item>
          </DropdownButton>
        </Form.Group>
        <Form.Group controlId="rating">
          <Form.Label>Rating</Form.Label>
          <Form.Control
            type="number"
            name="rating"
            value={feedbackData.rating}
            onChange={handleChange}
            min="1"
            max="5"
            placeholder="Rate from 1 to 5"
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">Submit</Button>
      </Form>
    </div>
  );
};

export default FeedbackForm;
