import React, { useState } from 'react';
import { loginStudent } from './studentService';

const StudentLogin = () => {
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [studentInfo, setStudentInfo] = useState(null);
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await loginStudent(credentials);

      console.log('Response:', response); // Debugging: log the response

      if (response.data && response.data.student_id) {
        setStudentInfo(response.data);
        alert('Login successful!');
      } else {
        throw new Error('Unexpected response format');
      }
    } catch (error) {
      console.error('There was an error logging in!', error);
      setError('Login failed. Please check your credentials and try again.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  return (
    <div>
      <h2>Student Login</h2>
      <input
        type="text"
        name="username"
        value={credentials.username}
        onChange={handleChange}
        placeholder="Username"
      />
      <input
        type="password"
        name="password"
        value={credentials.password}
        onChange={handleChange}
        placeholder="Password"
      />
      <button onClick={handleLogin}>Login</button>
      {error && <p className="error">{error}</p>}
      {studentInfo && (
        <div className="mt-4">
          <h3>Student Details</h3>
          <p><strong>Student ID:</strong> {studentInfo.student_id}</p>
          <p><strong>Name:</strong> {studentInfo.student_name}</p>
          <p><strong>Phone Number:</strong> {studentInfo.phone_no}</p>
          <p><strong>Department:</strong> {studentInfo.department}</p>
          <p><strong>Marks:</strong> {studentInfo.marks}</p>
          <p><strong>Email:</strong> {studentInfo.email}</p>
        </div>
      )}
    </div>
  );
};

export default StudentLogin;
